#!/bin/bash

set -e

echo "🚀 Starting optimized deployment with automatic font installation..."

# Configuration
IMAGE_NAME="text-to-image-service"
CONTAINER_NAME="text-to-image-app"
PORT="3000"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

print_status() {
    echo -e "${BLUE}[INFO]${NC} $1"
}

print_success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

print_warning() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

print_error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

# Stop and remove existing container
print_status "Stopping existing container..."
docker stop $CONTAINER_NAME 2>/dev/null || true
docker rm $CONTAINER_NAME 2>/dev/null || true

# Build the image
print_status "Building Docker image with fonts..."
docker build -t $IMAGE_NAME . || {
    print_error "Failed to build Docker image"
    exit 1
}

# Create and start the container
print_status "Starting container..."
docker run -d \
    --name $CONTAINER_NAME \
    -p $PORT:3000 \
    --restart unless-stopped \
    --health-cmd="curl -f http://localhost:3000/api/health/fonts || exit 1" \
    --health-interval=30s \
    --health-timeout=10s \
    --health-retries=3 \
    $IMAGE_NAME || {
    print_error "Failed to start container"
    exit 1
}

# Wait for container to be ready
print_status "Waiting for service to be ready..."
sleep 10

# Check container status
if docker ps | grep -q $CONTAINER_NAME; then
    print_success "Container is running"
else
    print_error "Container failed to start"
    docker logs $CONTAINER_NAME
    exit 1
fi

# Test font health
print_status "Testing font health..."
for i in {1..10}; do
    if curl -s http://localhost:$PORT/api/health/fonts > /dev/null; then
        print_success "Font health check passed"
        break
    else
        if [ $i -eq 10 ]; then
            print_error "Font health check failed after 10 attempts"
            docker logs $CONTAINER_NAME
            exit 1
        fi
        print_status "Waiting for service... (attempt $i/10)"
        sleep 5
    fi
done

# Get font health status
print_status "Checking font status..."
FONT_STATUS=$(curl -s http://localhost:$PORT/api/health/fonts | jq -r '.status' 2>/dev/null || echo "unknown")

case $FONT_STATUS in
    "healthy")
        print_success "All fonts are working correctly"
        ;;
    "degraded")
        print_warning "Some fonts are missing but service is functional"
        ;;
    "unhealthy")
        print_error "Font system is not working properly"
        ;;
    *)
        print_warning "Could not determine font status"
        ;;
esac

# Test Chinese character rendering
print_status "Testing Chinese character rendering..."
TEST_RESPONSE=$(curl -s -X POST http://localhost:$PORT/api/generate-image \
    -H "Content-Type: application/json" \
    -d '{"content":"你好世界 Hello World","format":"text","theme":"cute"}' \
    --write-out "%{http_code}" \
    --output /tmp/test-image.png)

if [ "$TEST_RESPONSE" = "200" ]; then
    print_success "Chinese character rendering test passed"
    print_status "Test image saved to /tmp/test-image.png"
else
    print_error "Chinese character rendering test failed (HTTP $TEST_RESPONSE)"
fi

# Display deployment information
echo ""
